const StoryblokClient = require("storyblok-js-client");

/* Initiating Storyblok client, so that we are 
connected to our Storyblok account and space.
Please replace this accessToken with your own one.*/
const Storyblok = new StoryblokClient({
  accessToken: "CQ5CbFz9QXEJ0enlvMIWNwtt",
  cache: {
    clear: "auto",
    type: "memory"
  }
});

export default async (req, res) => {
  Storyblok.get(`cdn/stories`, { starts_with: `root` })
    .then(response => {
      const {
        data: { stories }
      } = response
      const data = { stories }
      res.status(200).json(data)
    })
    .catch(error => {
      console.log(error)
      res.status(500).json(`${error.name}: ${error.message}`)
    })
}
