/* styles */
import style from './blogpost.scss';

const BlogPost = ({ title, intro, body, img }) => {
	return (
		<div className={style.root}>
			<h2>{title}</h2>
            <p>{intro}</p>
			<p className="blog-text">{body}</p>

			<div className="image-wrapper">
			<img src={img}/>
			</div>
		</div>
	);
};

export default BlogPost;
